import React, { useState } from 'react';
import styled from 'styled-components';
import styles from './StyleModul.module.scss';
// import './StyleModul.module.css';

interface Inav {
  onClickLog: () => void;
  onClickReg: () => void;
}

const Component1: React.FC<Inav> = ({onClickLog, onClickReg}) => {
  const [state, setState] = useState(false);
  return (
  //   <div className={`${styles.main} ${state ? styles.size : styles.size__min}`} onClick={() => setState(!state)}>
  //    <h1> Task Board</h1>
  //     <nav>
  //       <a href="/login">Login</a>
  //       <a href="/registartion">Registration</a>
  //     </nav>
  //   </div>
  // );}
    <StyledWrapper onClick={() => setState(!state)} status={state}>
      <div className="main__Logo">
      <StyledH1 >
        <a href="/">Task Board</a>
        </StyledH1>

      <h2 className="main__title">Create your New work space</h2>
      </div>
     <div className="main__navigation">
      <a href="/login" onClick={onClickLog}>Login</a>
      <a href="/registartion" onClick={onClickReg}>Registration</a>
      </div>
    </StyledWrapper>

  )
}

type StylesProps = {
  status: boolean;
}

export const StyledWrapper = styled.div<StylesProps>`
  background-color: #d1b49c57;
  height: ${props => props.status ? 400 : 80}px;
  width: 100%;
  display:flex;
  align-items: center;
  justify-content: space-between;
  .h1style {

  }
  .main {
    &__Logo{

    }

    &__title {
      color: white;
      margin-left: 1rem;
    }
    &__navigation{
      display: flex;
      justify-content: space-between;
      margin-right: 20px;

      width: 8%;
      a{
      text-decoration: none;
      color: black;
    }
    }
  }
`;

const StyledH1 = styled.h1`
  a{
    color: #020202;
    margin-left: 2rem;
    text-decoration:none;
  }
  a::hover{
    transform: scale(120%);
  }
`;

const divLoginRegistration = styled.div `
  display: flex;
`

export default Component1;
