import React, { useState } from 'react';
import './mainStyle.css';

import UserRegistrationForm from '../userRegistrationForm/UserRegistrationForm';
import UserLoginForm from '../loginForm/loginForm';
import Header from '../header/header';

import Component1 from '../styleComponent/cssModul';


function Main () {
  const [logForm, setLogForm] = useState(false);
  const [regForm, setRegForm] = useState(false);
  const onClickLog = (event) => {
    setLogForm(!logForm);
    event.preventDefault();
        if(regForm){
      setRegForm(!regForm);
      event.preventDefault();
    }
  }
  const onClickReg = () => {
    setRegForm(!regForm);
    if(logForm){
      setLogForm(!logForm);
    }
  }
  return (
    <body>
    <Component1 
    onClickLog={onClickLog}
    onClickReg={onClickReg}
    />
<main>
<Header onClickLog={onClickLog}
  onClickReg={onClickReg}
/>
{regForm && <UserRegistrationForm
    onClickReg={onClickReg}
/>}
{logForm && <UserLoginForm
    onClickLog={onClickLog}
/>}
</main>
</body>
    );
}

export default Main;
