import styled from "styled-components";

type StylesProps = {
  status: boolean;
}

export const StyledWrapper = styled.div<StylesProps>`
nav-bar{
&__logo{

}
&__title{

  &-hiden {

  }
}

}
  background-color: #d1b49c57;
  height: ${props => props.status ? 400 : 80}px;
  width: 100%;
  display:flex;
  align-items: center;
  justify-content: space-between;
  .h1style {

  }
  .main {
    &__Logo{

    }

    &__title {
      color: white;
      margin-left: 1rem;
    }
    &__navigation{
      display: flex;
      justify-content: space-between;
      margin-right: 20px;

      width: 8%;
      a{
      text-decoration: none;
      color: black;
    }
    }
  }
`;

export const StyledH1 = styled.h1`
  a {
    color: #020202;
    margin-left: 2rem;
    text-decoration:none;
    &::hover{
    transform: scale(120%);
  }
  }

`;

export const divLoginRegistration = styled.div `
  display: flex;
`
